import React from "react";
import { View, Text, Image, TouchableOpacity, StyleSheet } from "react-native";

export default function Item({ item }) {
  const decimals = (num) => {
    let number = Number(num).toFixed(2);
    return number;
  };

  return (
    <View style={styles.listItem}>
      <View style={styles.symbol}>
        <Text style={{ fontWeight: "bold" }}>{item.symbol}</Text>
      </View>

      <View style={{ alignItems: "center", flex: 1 }}>
        <Text style={{ fontWeight: "bold" }}>Price</Text>
        <Text style={{ fontWeight: "bold" }}>${decimals(item.priceUsd)}</Text>
      </View>
      <TouchableOpacity
        style={{
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <View style={{ alignItems: "center", flex: 1 }}>
          <Text style={{ fontWeight: "bold", marginRight: 12 },decimals(item.changePercent24Hr)>0 ? {color: "green"}:{color: "red"}}>
            change Percent
          </Text>
          <Text style={{ fontWeight: "bold",  marginRight: 12 }}>
            {decimals(item.changePercent24Hr)}%
          </Text>
        </View>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  listItem: {
    margin: 10,
    padding: 10,
    backgroundColor: "#FFF",
    width: "90%",
    flex: 1,
    alignSelf: "center",
    flexDirection: "row",
    borderRadius: 5,
  },
  symbol: {
    width: 60,
    height: 60,
    borderRadius: 30,
    alignItems: "center",
    backgroundColor: "#9fb2d1",
    justifyContent: "center",
  },
});
