import React, { useEffect, useState } from "react";
import { View, Text, FlatList, StyleSheet } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { listCurrencies } from "../actions/currencyActions";
import Item from "./Item";

export default function Currencies() {


  const dispatch = useDispatch();
  const currencyList = useSelector((state) => state.currencyList);
  const { currencies, loading, error } = currencyList;
  const items = [];
  currencies &&
    currencies.slice(0, 10).map((item) => {
      items.push(item);
    });

    console.log(items);
  useEffect(() => {
    dispatch(listCurrencies());
  }, [dispatch]);

  return (
    <View style={styles.container}>
      <FlatList
        style={{ flex: 1 }}
        data={items}
        renderItem={({ item }) => <Item item={item} />}
        keyExtractor={(item) => item.id}
      />
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#F7F7F7",
  },
});
