import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  Dimensions,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import { LineChart } from "react-native-chart-kit";
import { useDispatch, useSelector } from "react-redux";
import { currencyDetail } from "../actions/currencyActions";
import Slider from "@react-native-community/slider";

const SingleCurrency = (props) => {
  const { id, price } = props.route.params;
  const [sel, setSell] = useState(false);
  const [buy, setBuy] = useState(false);
  const [userInfo, setUserInfo] = useState({});
  const [solde, setSolde] = useState(0);
  const [soldeBuy, setSoldeBuy] = useState(0);
  const [calc, setCalc] = useState(0);
  const [localCrncy, setLocalCrncy] = useState("");

  const dispatch = useDispatch();
  const currencyDetails = useSelector((state) => state.currencyDetails);
  const { loading, currency, error } = currencyDetails;
  const user = useSelector((state) => state.user);
  var result = [];
  let dates = [];
  let prices = [];
  var nowHour = new Date().getHours();

  for (var i = nowHour; i <= 24; i++) {
    if (i > 12) {
      result.push(i + "PM");
    } else {
      result.push(i + "AM");
    }
  }

  // const decimals = (num) => {
  //   let number = parseFloat(num).toFixed(0);
  //   return number;
  // };

  function msToTime(duration) {
    var hours = Math.floor((duration / (1000 * 60 * 60)) % 24);
    hours = hours < 10 ? "0" + hours : hours;
    return hours;
  }

  const sell = () => {
    setSell(!sel);
  };
  const buySection = () => {
    setBuy(!buy);
  };
  console.log(soldeBuy);
  currency &&
    currency.map((dataC) => {
      const time = msToTime(dataC.time);
      dates.push(time);
      prices.push(parseInt(dataC.priceUsd));
    });

  const calculValue = (soldeBuy, price) => {
    return (soldeBuy * 1) / price;
  };
  useEffect(() => {
    dispatch(currencyDetail(id));
    if (user) {
      setUserInfo(user.userInfo);
      setLocalCrncy(user.userInfo.localCrncy);
      setSolde(user.userInfo.solde);
    }
    console.log(localCrncy, solde);
  }, [dispatch, userInfo, user, solde, localCrncy]);

  return (
    <View style={{ alignItems: "center" }}>
      <View style={{ flexDirection: "row" }}>
        <Text style={{ fontSize: 20, padding: 20, color: "black" }}>
          {id.toUpperCase()}
        </Text>
        <Text style={{ fontSize: 18, padding: 20, color: "black" }}>
          ${price}
        </Text>
      </View>

      {userInfo && (
        <View
          style={{
            alignItems: "center",
            backgroundColor: "#003f5c",
            height: 50,
            width: "95%",
            flexDirection: "row",
            justifyContent: "space-between",
            padding: 20,
            borderRadius: 36,
          }}
        >
          <Text style={{ color: "white" }}>Solde : {solde}</Text>
          <Text style={{ color: "white" }}>Currency : {localCrncy}</Text>
        </View>
      )}

      {currency !== null ? (
        <LineChart
          data={{
            labels: dates && dates,
            datasets: [
              {
                data: prices && prices,
              },
            ],
          }}
          width={Dimensions.get("window").width - 35} // from react-native
          height={300}
          yAxisLabel="$"
          yAxisSuffix="k"
          yAxisInterval={1} // optional, defaults to 1
          chartConfig={{
            backgroundColor: "#e26a00",
            backgroundGradientFrom: "#003f5c",
            backgroundGradientTo: "#003f5c",
            decimalPlaces: 2, // optional, defaults to 2dp
            color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
            labelColor: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
            style: {
              borderRadius: 16,
            },
            propsForDots: {
              r: "6",
              strokeWidth: ".5",
              stroke: "#ffa726",
            },
          }}
          bezier
          style={{
            marginVertical: 8,
            borderRadius: 16,
          }}
        />
      ) : (
        <Text>no data here</Text>
      )}

      <TouchableOpacity style={styles.loginBtn} onPress={() => sell()}>
        <Text style={{ color: "white" }}>SELL</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.loginBtn} onPress={() => buySection()}>
        <Text style={{ color: "white" }}>BUY</Text>
      </TouchableOpacity>
      {sel && (
        <View style={{ marginTop: 24 }}>
          <Slider
            style={{ width: 200, height: 40 }}
            minimumValue={0}
            maximumValue={solde && solde}
            minimumTrackTintColor="#FFFFFF"
            maximumTrackTintColor="#000000"
          />
        </View>
      )}

      {buy && (
        <View>
          <Slider
            style={{ width: 200, height: 40 }}
            minimumValue={0}
            maximumValue={solde && solde}
            minimumTrackTintColor="#FFFFFF"
            maximumTrackTintColor="#000000"
            onValueChange={(soldeBuy) => {
              setSoldeBuy(soldeBuy);
            }}
          />
          {soldeBuy !== 0 && <Text>Solde : {soldeBuy}</Text>}
          {soldeBuy !== 0 && (
            <Text>Currency value :{calculValue(soldeBuy, price)}</Text>
          )}
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  loginBtn: {
    width: "80%",
    backgroundColor: "#003f5c",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10,
  },
});
export default SingleCurrency;
