import { createStore, combineReducers, applyMiddleware } from "redux";
import thunk from "redux-thunk";
import { composeWithDevTools } from "redux-devtools-extension";
import { currencyDetailsReducer, currencyListReducer } from "./reducers/currencyReducers";
import { addUserReducer, userDetailsReducer, userLoginReducer, userRegisterReducer } from "./reducers/userReducers";

const middleware = [thunk];

const userInfoFromStorage = null;
const initialState = {
  userLogin: {
    userInfo: userInfoFromStorage,
  },
  userInfo
};
const reducer = combineReducers({
  currencyList: currencyListReducer,
  userRegister: userRegisterReducer,
  userLogin: userLoginReducer,
  currencyDetails: currencyDetailsReducer,
  userInfo:addUserReducer || userLoginReducer
});

const store = createStore(
  reducer,
  initialState,
  composeWithDevTools(applyMiddleware(...middleware))
);

export default store;
