import {
  USER_LOGIN_FAIL,
  USER_LOGIN_REQUEST,
  USER_LOGIN_SUCCESS,
  USER_REGISTER_FAIL,
  USER_REGISTER_REQUEST,
  USER_REGISTER_SUCCESS,
} from "../constants/constants";
import firebase from "../configs/firebase";
import AsyncStorage from "@react-native-async-storage/async-storage";

export const register = (email, password) => async (dispatch) => {
  try {
    dispatch({
      type: USER_REGISTER_REQUEST,
    });

    await firebase
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then((data) => {
        dispatch({
          type: USER_REGISTER_SUCCESS,
          payload: data.user,
        });
        AsyncStorage.setItem("userInfo", JSON.stringify(data.user));
        console.log(JSON.parse(AsyncStorage.getItem("userInfo")));
      });
  } catch (error) {
    dispatch({
      type: USER_REGISTER_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const login = (email, password) => async (dispatch) => {
  try {
    dispatch({
      type: USER_LOGIN_REQUEST,
    });

    await firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(async (data) => {
        dispatch({
          type: USER_LOGIN_SUCCESS,
          payload: data.user,
        });
        let ddd = await (AsyncStorage.getItem("userInfo"));
        console.log(ddd);
        // AsyncStorage.setItem("userInfo", JSON.stringify(data.user));
      });
  } catch (error) {
    dispatch({
      type: USER_LOGIN_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const add = (f_uid) => async (dispatch) => {
  try {
    dispatch({
      type: ADD_USER_REQUEST,
    });

    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const { data } = await axios.post(
      "http://localhost:5000/user/add/" + f_uid,
      config
    );

    dispatch({
      type: ADD_USER_SUCCESS,
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: ADD_USER_FAIL,
      payload: error,
    });
  }
};
