import React, { useEffect } from "react";
import { View, Text, Dimensions } from "react-native";
import {
  LineChart,
  BarChart,
  PieChart,
  ProgressChart,
  ContributionGraph,
  StackedBarChart,
} from "react-native-chart-kit";
import {
  Chart,
  Line,
  Area,
  HorizontalAxis,
  VerticalAxis,
} from "react-native-responsive-linechart";

import { useDispatch, useSelector } from "react-redux";
import { currencyDetail } from "../actions/currencyActions";

const SingleCurrency = (props) => {
  const { id, price } = props.route.params;
  const dispatch = useDispatch();
  const currencyDetails = useSelector((state) => state.currencyDetails);
  const { loading, currency, error } = currencyDetails;

  var result = [];
  let dates = [];
  let prices = [];
  var nowHour = new Date().getHours();

  for (var i = nowHour; i <= 24; i++) {
    if (i > 12) {
      result.push(i + "PM");
    } else {
      result.push(i + "AM");
    }
  }

  const decimals = (num) => {
    let number = parseFloat(num).toFixed(0);
    return number;
  };

  function msToTime(duration) {
    var hours = Math.floor((duration / (1000 * 60 * 60)) % 24);

    hours = hours < 10 ? "0" + hours : hours;

    return hours;
  }

  currency &&
    currency.map((dataC) => {
      const time = msToTime(dataC.time);
      dates.push(time);
      prices.push(parseInt(dataC.priceUsd));
      // console.log(typeof parseInt(decimals(dataC.priceUsd)));
    });
  useEffect(() => {
    dispatch(currencyDetail(id));
  }, [dispatch]);

  return (
    <View style={{ alignItems: "center" }}>
      <Text style={{ fontSize: 20, padding: 20 }}>{id.toUpperCase()}</Text>
      {/* <LineChart
        data={{
          labels: dates && dates,
          datasets: [
            {
              data: prices && prices,
            },
          ],
        }}
        width={Dimensions.get("window").width - 35} // from react-native
        height={300}
        yAxisLabel="$"
        yAxisSuffix="k"
        yAxisInterval={1} // optional, defaults to 1
        chartConfig={{
          backgroundColor: "#e26a00",
          backgroundGradientFrom: "#003f5c",
          backgroundGradientTo: "#003f5c",
          decimalPlaces: 2, // optional, defaults to 2dp
          color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
          labelColor: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
          style: {
            borderRadius: 16,
          },
          propsForDots: {
            r: "6",
            strokeWidth: ".5",
            stroke: "#ffa726",
          },
        }}
        bezier
        style={{
          marginVertical: 8,
          borderRadius: 16,
        }}
      /> */}
      <Chart
        style={{ height: 200, width: 400 }}
        data={[
          
          { x: -2, y: 15 },
          { x: -1, y: 10 },
          { x: 0, y: 12 },
          { x: 1, y: 7 },
          { x: 2, y: 6 },
          { x: 3, y: 8 },
          { x: 4, y: 10 },
          { x: 5, y: 8 },
          { x: 6, y: 12 },
          { x: 7, y: 14 },
          { x: 8, y: 12 },
          { x: 9, y: 13.5 },
          { x: 10, y: 18 },
        ]}
        padding={{ left: 40, bottom: 20, right: 20, top: 20 }}
        xDomain={{ min: -2, max: 10 }}
        yDomain={{ min: 0, max: 20 }}
      >
        <VerticalAxis
          tickCount={11}
          theme={{ labels: { formatter: (v) => v.toFixed(2) } }}
        />
        <HorizontalAxis tickCount={5} />
        <Area
          theme={{
            gradient: {
              from: { color: "#ffa502" },
              to: { color: "#ffa502", opacity: 0.4 },
            },
          }}
        />
        <Line
          theme={{
            stroke: { color: "#ffa502", width: 5 },
            scatter: { default: { width: 4, height: 4, rx: 2 } },
          }}
        />
      </Chart>
    </View>
  );
};
export default SingleCurrency;
