import React from "react";
import { View, Text, Image, TouchableOpacity, StyleSheet } from "react-native";

export default function Item({ item }) {
  return (
    <View style={styles.listItem}>
      {/* <Image
        source={{ uri: item.photo }}
        style={{  }}
      /> */}
        <Text style={{ fontWeight: "bold",width: 60, height: 60, borderRadius: 30,justifyContent:'center' }}>{item.symbol}</Text>
   

      <View style={{ alignItems: "center", flex: 1 }}>
        <Text style={{ fontWeight: "bold", color: "red" }}>{item.name}</Text>
        <Text style={{ fontWeight: "bold", color: "red" }}>1%</Text>
      </View>
      <TouchableOpacity
        style={{
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <View style={{ alignItems: "center", flex: 1 }}>
          <Text style={{ fontWeight: "bold", color: "green", marginRight: 12 }}>
            {item.name}
          </Text>
          <Text style={{ fontWeight: "bold", color: "green", marginRight: 12 }}>
            1%
          </Text>
        </View>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  listItem: {
    margin: 10,
    padding: 10,
    backgroundColor: "#FFF",
    width: "90%",
    flex: 1,
    alignSelf: "center",
    flexDirection: "row",
    borderRadius: 5,
  },
});
