import React, { useEffect, useState } from "react";
import { View, Text, FlatList, StyleSheet } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { listCurrencies } from "../actions/currencyActions";
import Item from "./Item";

export default function Currencies() {
  const [data, setData] = useState([
    {
      name: "Miyah Myles",
      email: "miyah.myles@gmail.com",
      position: "Data Entry Clerk",
      photo:
        "https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-0.3.5&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=200&fit=max&s=707b9c33066bf8808c934c8ab394dff6",
    },
    {
      name: "June Cha",
      email: "june.cha@gmail.com",
      position: "Sales Manager",
      photo: "https://randomuser.me/api/portraits/women/44.jpg",
    },
  ]);

  const dispatch = useDispatch();
  const currencyList = useSelector((state) => state.currencyList);
  const { currencies, loading, error } = currencyList;
  const items = [];
  currencies &&
    currencies.slice(0, 10).map((item) => {
      items.push(item);
    });

    console.log(items);
  useEffect(() => {
    dispatch(listCurrencies());
  }, [dispatch]);

  return (
    <View style={styles.container}>
      <FlatList
        style={{ flex: 1 }}
        data={data}
        renderItem={({ item }) => <Item item={item} />}
        keyExtractor={(item) => item.email}
      />
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#F7F7F7",
  },
});
