import React, { useEffect } from "react";
import { View, Text, Dimensions } from "react-native";
import {
  LineChart,
  BarChart,
  PieChart,
  ProgressChart,
  ContributionGraph,
  StackedBarChart,
} from "react-native-chart-kit";
import { useDispatch, useSelector } from "react-redux";
import { currencyDetail } from "../actions/currencyActions";

const SingleCurrency = (props) => {
  const { id, price } = props.route.params;
  const dispatch = useDispatch();
  const currencyDetails = useSelector((state) => state.currencyDetails);
  const { loading, currency, error } = currencyDetails;

  var result = [];
  let dates = [];
  let prices = [];
  var nowHour = new Date().getHours();

  for (var i = nowHour; i <= 24; i++) {
    if (i > 12) {
      result.push(i + "PM");
    } else {
      result.push(i + "AM");
    }
  }

  const decimals = (num) => {
    let number = Number(num).toFixed(2);
    return number;
  };

  function msToTime(duration) {
    var seconds = Math.floor((duration / 1000) % 60),
      minutes = Math.floor((duration / (1000 * 60)) % 60),
      hours = Math.floor((duration / (1000 * 60 * 60)) % 24);

    hours = hours < 10 ? "0" + hours : hours;
    minutes = minutes < 10 ? "0" + minutes : minutes;
    seconds = seconds < 10 ? "0" + seconds : seconds;

    return hours + ":" + minutes;
  }

  currency.map((dataC) => {
    const time = msToTime(dataC.time);
    dates.push(time.time);
    prices.push(decimals(dataC.priceUsd));
  });
  useEffect(() => {
    dispatch(currencyDetail(id));
  }, [dispatch]);

  return (
    <View style={{ alignItems: "center" }}>
      <Text style={{ fontSize: 20, padding: 20 }}>{id.toUpperCase()}</Text>
      <Text style={{ fontSize: 20, padding: 20 }}>{price}</Text>
      {/* <LineChart
        data={{
          labels: dates,
          datasets: [
            {
              data: prices,
            },
          ],
        }}
        width={Dimensions.get("window").width - 35} // from react-native
        height={300}
        yAxisLabel="$"
        yAxisSuffix="k"
        yAxisInterval={1} // optional, defaults to 1
        chartConfig={{
          backgroundColor: "#e26a00",
          backgroundGradientFrom: "#003f5c",
          backgroundGradientTo: "#003f5c",
          decimalPlaces: 2, // optional, defaults to 2dp
          color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
          labelColor: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
          style: {
            borderRadius: 16,
          },
          propsForDots: {
            r: "6",
            strokeWidth: ".5",
            stroke: "#ffa726",
          },
        }}
        bezier
        style={{
          marginVertical: 8,
          borderRadius: 16,
        }}
      /> */}
    </View>
  );
};
export default SingleCurrency;
