import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  Dimensions,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import {
  LineChart,
  BarChart,
  PieChart,
  ProgressChart,
  ContributionGraph,
  StackedBarChart,
} from "react-native-chart-kit";
import { useDispatch, useSelector } from "react-redux";
import { currencyDetail } from "../actions/currencyActions";

const SingleCurrency = (props) => {
  const { id, price } = props.route.params;
  const [sel, setSell] = useState(false);
  const dispatch = useDispatch();
  const currencyDetails = useSelector((state) => state.currencyDetails);
  const { loading, currency, error } = currencyDetails;
  const user = useSelector((state) => state.user);

  // const { userInfo } = user;
  const [userInfo, setUserInfo] = useState({});

  var result = [];
  let dates = [];
  let prices = [];
  var nowHour = new Date().getHours();

  for (var i = nowHour; i <= 24; i++) {
    if (i > 12) {
      result.push(i + "PM");
    } else {
      result.push(i + "AM");
    }
  }

  const decimals = (num) => {
    let number = parseFloat(num).toFixed(0);
    return number;
  };

  function msToTime(duration) {
    var hours = Math.floor((duration / (1000 * 60 * 60)) % 24);

    hours = hours < 10 ? "0" + hours : hours;

    return hours;
  }

  const sell = () => {
    setSell(true);
  };

  currency &&
    currency.map((dataC) => {
      const time = msToTime(dataC.time);
      dates.push(time);
      prices.push(parseInt(dataC.priceUsd));
      // console.log(typeof parseInt(decimals(dataC.priceUsd)));
    });
  useEffect(() => {
    dispatch(currencyDetail(id));
    if (user) {
      setUserInfo(user.userInfo);
    }
    console.log(userInfo.solde);
  }, [dispatch, userInfo,user]);

  return (
    <View style={{ alignItems: "center" }}>
      <View style={{ flexDirection: "row" }}>
        <Text style={{ fontSize: 20, padding: 20, color: "black" }}>
          {id.toUpperCase()}
        </Text>
        <Text style={{ fontSize: 18, padding: 20, color: "black" }}>
          ${price}
        </Text>
      </View>

      {userInfo !== undefined && (
        <View
          style={{
            alignItems: "center",
            backgroundColor: "#003f5c",
            height: 50,
            width: "95%",
            flexDirection: "row",
            justifyContent: "space-between",
            padding: 20,
            borderRadius: 36,
          }}
        >
          <Text style={{ color: "white" }}>Solde : {userInfo.solde}</Text>
          <Text style={{ color: "white" }}>
            Currency : {userInfo.localCrncy}
          </Text>
        </View>
      )}

      {currency !== null ? (
        <LineChart
          data={{
            labels: dates && dates,
            datasets: [
              {
                data: [12, 232323, 344, 3434, 455, 12],
              },
            ],
          }}
          width={Dimensions.get("window").width - 35} // from react-native
          height={300}
          yAxisLabel="$"
          yAxisSuffix="k"
          yAxisInterval={1} // optional, defaults to 1
          chartConfig={{
            backgroundColor: "#e26a00",
            backgroundGradientFrom: "#003f5c",
            backgroundGradientTo: "#003f5c",
            decimalPlaces: 2, // optional, defaults to 2dp
            color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
            labelColor: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
            style: {
              borderRadius: 16,
            },
            propsForDots: {
              r: "6",
              strokeWidth: ".5",
              stroke: "#ffa726",
            },
          }}
          bezier
          style={{
            marginVertical: 8,
            borderRadius: 16,
          }}
        />
      ) : (
        <Text>no data here</Text>
      )}

      <TouchableOpacity style={styles.loginBtn} onPress={() => sell()}>
        <Text style={{ color: "white" }}>SELL</Text>
      </TouchableOpacity>
      {sel && <Text>sell</Text>}
    </View>
  );
};

const styles = StyleSheet.create({
  loginBtn: {
    width: "80%",
    backgroundColor: "#003f5c",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10,
  },
});
export default SingleCurrency;
